/* large 1024 bit prime p for which (p-1)/2 is also prime */
char *primetext=
//From RFC 3256
/*4096 bit*/ //"";
/*3072 bit*/ //"";
/*2048 bit*/ //"";
/*1526 bit*/ //"";
//From RFC 2409
/*1024 bit*/ "";
/* 768 bit*/ //"1552518092300708935130918131258481755631334049434514313202351194902966239949102107258669453876591642442910007680288864229150803718918046342632727613031282983744380820890196288509170691316593175367469551763119843371637221007210577919";

int main()
{
    int ia,ib;
    time_t seed;
    big a,b;
    struct timeval tv1, tv2;
    double first, second;  
    miracl *mip;
    char x;
    
#ifndef MR_NOFULLWIDTH   
    mip=mirsys(256,0);
#else
    mip=mirsys(256,MAXBASE);
#endif
/* mirsys sets the size for which each big number is to be stored within. 
 * It's a fixed size for all big numbers used during that initialistion.
 * When a negative number is entered, it's accepting a specified number of bytes.
 * 
 * In my attempts with the above numbers it needs:
 * 		- 487 (i.e. 487 byte maximum) for 1526 bit number
 * 		- 154 for 1024 bit number
 * 		- crashes if any number greater than 757 is used, making 2048 bit and above impossible
 */

    a=mirvar(0);
    b=mirvar(0);

    time(&seed);
    irand((unsigned long)seed);   /* change parameter for different values */

	gettimeofday(&tv1, NULL);

	first = 
			 (tv1.tv_sec) * 1000 + (tv1.tv_usec) / 1000 ;
	printf("\nTime 1: %.0lf", first);
	printf("\nSeconds %d\t|Microseconds%d", (int) tv1.tv_sec, (int) tv1.tv_usec);


    printf("Reading in number\n");

    cinstr(a,primetext);
    
    printf("Displaying number=\n");
    cotnum(a,stdout);
    
    scanf("%c", &x);
    
	gettimeofday(&tv2, NULL);
    second = 
			 (tv2.tv_sec) * 1000 + (tv2.tv_usec) / 1000 ;//seconds and microseconds
	printf("\nTime 1: %.0lf", second);
	printf("\nSeconds %d\t|Microseconds %d", (int) tv2.tv_sec, (int) tv2.tv_usec);
	
	
	printf("\nDifference: %.0lf", (second-first));
	printf("\nSeconds %d\t|Microseconds %d\n", (int) (tv2.tv_sec-tv1.tv_sec), (int) (tv2.tv_usec-tv1.tv_usec));

    return 0;
}

